package de.nycode.mcserver.packets.impl.status

import de.nycode.mcserver.ClientHandler
import de.nycode.mcserver.packets.Packet
import de.nycode.mcserver.writeString
import java.io.DataInputStream
import java.io.DataOutputStream

class StatusResponsePacket(client: ClientHandler) : Packet(client) {

    override val packetId = 0x00

    private val status = "{" +
            "    \"version\": {" +
            "        \"name\": \"${client.protocolVersion?.number}\"," +
            "        \"protocol\": ${client.protocolVersion?.number}" +
            "    }," +
            "    \"players\": {" +
            "        \"max\": 0," +
            "        \"online\": 0," +
            "        \"sample\": [" +
            "        ]" +
            "    }," +
            "    \"description\": {" +
            "        \"text\": \"§7Hello §a${client.connectionAddress}\n§9Minecraft Server by NyCode\"" +
            "    }," +
            "\"favicon\":\"data:image/png;base64,\"" +
            "}"

    override fun read(input: DataInputStream) {
    }

    override fun write(output: DataOutputStream) {
        output.writeString(status)
    }
}
